import React, { useState } from "react";
import styles from "./HeaderCartButton.module.css";
import { FaShoppingCart } from "@react-icons/all-files/fa/FaShoppingCart";
import Cart from "../Cart/Cart";

const HeaderCartButton = (props) => {
  const [isCartButtonClicked, setIsCartButtonClicked] = useState(false);

  const cartButtonHandler = () => {
    setIsCartButtonClicked(true);
  };

  return (
    <button className={styles.button} onClick={cartButtonHandler}>
      <FaShoppingCart className={styles.icon} /> Your Cart
      <span className={styles.badge}>7amada</span>
      {isCartButtonClicked && <Cart />}
    </button>
  );
};

export default HeaderCartButton;
