import React, { useRef } from "react";
import Input from "../../UI/Input";
import styles from "./MealItemForm.module.css";

export const ShoppingCartValue = React.createContext();

const MealItemForm = (props) => {
  const inputValue = useRef();
  const handleSubmit = (e) => {
    e.preventDefault();
    const amountValue = inputValue.current.value;
  };

  return (
    <ShoppingCartValue.Provider>
      <form className={styles.form} onSubmit={handleSubmit}>
        <Input
          id={props.id}
          input={{
            key: "props.id",
            min: "0",
            type: "number",
            step: "1",
            defaultValue: "0",
            ref: inputValue,
          }}
        />
        <button>+Add</button>
      </form>
    </ShoppingCartValue.Provider>
  );
};
export default MealItemForm;
