import React, { useState } from "react";
import styles from "./HeaderCartButton.module.css";
import { FaShoppingCart } from "@react-icons/all-files/fa/FaShoppingCart";
import Cart from "../Cart/Cart";

const HeaderCartButton = () => {
  const [cartIsRendered, setCartIsRendered] = useState(false);

  const HeaderCartButtonHandler = () => {
    setCartIsRendered(true);
  };

  return (
    <React.Fragment>
      <button className={styles.button} onClick={HeaderCartButtonHandler}>
        <FaShoppingCart className={styles.icon} /> Your Cart
        <span className={styles.badge}>7amada</span>
      </button>
      {cartIsRendered && <Cart />}
    </React.Fragment>
  );
};

export default HeaderCartButton;
